#!/usr/bin/env python3
"""
Create simple placeholder icons without PIL dependency
"""
import os
import base64

# Simple 1x1 pixel PNG in base64 (transparent)
TRANSPARENT_PNG = "iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVR42mNkYPhfDwAChAI9jM8lTgAAAABJRU5ErkJggg=="

# Simple colored PNG data for hotel icon
HOTEL_ICON_DATA = """

"""

# Create icons directory if it doesn't exist  
icons_dir = "/mnt/c/Users/T14/Desktop/hotel_flask_app/static/icons"
os.makedirs(icons_dir, exist_ok=True)

# Icon sizes needed
sizes = [16, 32, 72, 96, 128, 144, 152, 192, 384, 512]

# Create a simple colored square for each size (placeholder)
for size in sizes:
    # For now, create empty files as placeholders
    filename = f"icon-{size}x{size}.png"
    filepath = os.path.join(icons_dir, filename)
    
    # Write a simple placeholder PNG (1x1 transparent pixel)
    with open(filepath, 'wb') as f:
        f.write(base64.b64decode(TRANSPARENT_PNG))
    
    print(f"Created placeholder {filename}")

print(f"\nâœ… Placeholder PWA icons created in {icons_dir}")
print("Note: These are 1x1 transparent placeholders. Replace with actual hotel icons for production.")